import React from "react";
import { MessageCircle, Users, MoreVertical, LogOut, User, ArrowLeft, Shield } from "lucide-react";
import { Button } from "@/components/ui/button";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { User as UserEntity } from "@/entities/User";
import { Link } from "react-router-dom";
import { createPageUrl } from "@/utils";

export default function ChatHeader({ currentUser, selectedUser, selectedGroup, conversationType, onBack }) {
  const handleLogout = async () => {
    try {
      await UserEntity.logout();
      window.location.reload();
    } catch (error) {
      console.error("Erro ao fazer logout:", error);
    }
  };

  const getUserInitials = (name) => {
    if (!name) return "U";
    return name
      .split(" ")
      .map(n => n[0])
      .join("")
      .toUpperCase()
      .slice(0, 2);
  };

  const getUserColor = (userId) => {
    const colors = [
      "from-blue-500 to-blue-600", "from-green-500 to-green-600", 
      "from-purple-500 to-purple-600", "from-orange-500 to-orange-600", 
      "from-pink-500 to-pink-600", "from-indigo-500 to-indigo-600", 
      "from-red-500 to-red-600", "from-yellow-500 to-yellow-600"
    ];
    if (!userId) return colors[0];
    const index = userId.split('').reduce((acc, char) => acc + char.charCodeAt(0), 0);
    return colors[index % colors.length];
  };
  
  const getHeaderInfo = () => {
    if (conversationType === 'private' && selectedUser) {
      return {
        title: selectedUser.full_name,
        subtitle: selectedUser.email,
        avatar: getUserInitials(selectedUser.full_name),
        color: getUserColor(selectedUser.id)
      };
    } else if (conversationType === 'custom_group' && selectedGroup) {
      return {
        title: selectedGroup.name,
        subtitle: `${selectedGroup.members?.length || 0} membros`,
        avatar: selectedGroup.image_url ? null : <Users className="w-5 h-5 md:w-6 md:h-6 text-white" />,
        color: "from-purple-500 to-purple-600",
        image: selectedGroup.image_url
      };
    } else {
      return {
        title: "Chat Principal",
        subtitle: "Conversa geral",
        avatar: <Users className="w-5 h-5 md:w-6 md:h-6 text-white" />,
        color: "from-blue-500 to-blue-600"
      };
    }
  };

  const headerInfo = getHeaderInfo();

  return (
    <div className="border-b border-slate-200/60 bg-white/90 backdrop-blur-xl px-4 md:px-6 py-4">
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-3">
          <Button
            variant="ghost"
            size="icon"
            className="md:hidden text-slate-500 hover:bg-slate-100"
            onClick={onBack}
          >
            <ArrowLeft className="w-5 h-5" />
          </Button>

          <div className={`w-10 h-10 md:w-12 md:h-12 bg-gradient-to-br ${headerInfo.color} rounded-2xl flex items-center justify-center shadow-lg overflow-hidden`}>
            {headerInfo.image ? (
              <img src={headerInfo.image} alt={headerInfo.title} className="w-full h-full object-cover" />
            ) : headerInfo.avatar ? (
              typeof headerInfo.avatar === 'string' ? (
                <span className="text-base md:text-lg font-bold text-white">
                  {headerInfo.avatar}
                </span>
              ) : (
                headerInfo.avatar
              )
            ) : null}
          </div>

          <div className="min-w-0">
            <h1 className="text-base md:text-xl font-bold text-slate-900 truncate">
              {headerInfo.title}
            </h1>
            <p className="text-xs md:text-sm text-slate-500 truncate">
              {headerInfo.subtitle}
            </p>
          </div>
        </div>
        
        <div className="flex items-center gap-1 md:gap-3">
          <DropdownMenu>
            <DropdownMenuTrigger asChild>
              <Button variant="ghost" size="icon" className="text-slate-400 hover:text-slate-600 hover:bg-slate-100 rounded-xl">
                <MoreVertical className="w-5 h-5" />
              </Button>
            </DropdownMenuTrigger>
            <DropdownMenuContent align="end" className="w-56">
              {currentUser && (
                <>
                  <DropdownMenuItem disabled className="flex items-center gap-3 p-2">
                    <div className={`w-9 h-9 bg-gradient-to-br ${getUserColor(currentUser.id)} rounded-full flex items-center justify-center`}>
                        <span className="text-sm font-bold text-white">{getUserInitials(currentUser.full_name)}</span>
                    </div>
                    <div className="flex flex-col min-w-0">
                      <span className="font-medium truncate">{currentUser.full_name}</span>
                      <span className="text-xs text-slate-500 truncate">{currentUser.email}</span>
                    </div>
                  </DropdownMenuItem>
                  <DropdownMenuSeparator />
                </>
              )}
              {currentUser?.role === 'admin' && (
                <DropdownMenuItem asChild>
                  <Link to={createPageUrl('Admin')} className="flex items-center gap-2 cursor-pointer p-2">
                    <Shield className="w-4 h-4" />
                    Painel do Administrador
                  </Link>
                </DropdownMenuItem>
              )}
              <DropdownMenuItem 
                onClick={handleLogout}
                className="flex items-center gap-2 text-red-600 focus:text-red-600 cursor-pointer p-2"
              >
                <LogOut className="w-4 h-4" />
                Sair da Conta
              </DropdownMenuItem>
            </DropdownMenuContent>
          </DropdownMenu>
        </div>
      </div>
    </div>
  );
}
