import React, { useEffect, useState } from "react";

export default function UsersList({ currentUser, onSelectUser }) {
  const [users, setUsers] = useState([]);
  const [online, setOnline] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    async function fetchUsers() {
      try {
        const res = await fetch("/users");
        const data = await res.json();
        // Filtra apenas usuários aprovados e remove fakes
        const filtered = (data.users || []).filter(
          (u) => u.is_approved && !u.email.match(/^(alice|bob|carol)@/)
        );
        setUsers(filtered);
        const resOnline = await fetch("/online");
        const dataOnline = await resOnline.json();
        setOnline(dataOnline.online || []);
      } catch (err) {
        setError("Erro ao buscar usuários: " + (err.message || err));
      } finally {
        setLoading(false);
      }
    }
    fetchUsers();
  }, []);

  if (loading) return <div>Carregando usuários...</div>;
  if (error) return <div style={{ color: "red" }}>{error}</div>;

  return (
    <div>
      <div
        style={{
          fontWeight: "bold",
          marginBottom: 8,
        }}
      >
        Usuário logado: {currentUser?.nome || currentUser?.email}
      </div>
      <ul>
        {users.map((user) => (
          <li key={user.id} style={{ marginBottom: 6 }}>
            <button
              onClick={() => onSelectUser(user)}
              style={{
                fontWeight: user.id === currentUser?.id ? "bold" : "normal",
              }}
            >
              {user.nome || user.email}
              {online.includes(user.id) ? " ●" : ""}
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}
