import React, { useEffect, useState } from "react";

export default function UsersList() {
  const [users, setUsers] = useState([]);
  const [online, setOnline] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    async function fetchUsers() {
      try {
  const res = await fetch("/users");
        const data = await res.json();
        setUsers(data.users || []);
  const resOnline = await fetch("/online");
        const dataOnline = await resOnline.json();
        setOnline(dataOnline.online || []);
      } catch (err) {
        setError("Erro ao buscar usuários: " + (err.message || err));
      } finally {
        setLoading(false);
      }
    }
    fetchUsers();
  }, []);

  if (loading) return <div>Carregando usuários...</div>;
  if (error) return <div style={{ color: "red" }}>{error}</div>;

  // Filtra usuários online do domínio ceosoftware e clientes
  const onlineCeosoftware = online.filter(u => u.email && u.email.endsWith('@ceosoftware.com.br'));
  const onlineClientes = online.filter(u => u.email && !u.email.endsWith('@ceosoftware.com.br'));

  return (
    <div style={{ display: 'flex', gap: 32, minHeight: '100vh', backgroundImage: 'url(/assets/background.jpg)', backgroundSize: 'cover', backgroundPosition: 'center', backgroundRepeat: 'no-repeat' }}>
      <div style={{ flex: 1 }}>
        <h3 style={{ marginBottom: 8 }}>Analistas logados (ceosoftware)</h3>
        <ul style={{ background: '#f5f7ff', borderRadius: 8, padding: 12, minHeight: 80 }}>
          {onlineCeosoftware.length === 0 && <li style={{ color: '#64748b' }}>Nenhum analista logado</li>}
          {onlineCeosoftware.map(u => (
            <li key={u.email} style={{ padding: 4 }}>{u.email}</li>
          ))}
        </ul>
      </div>
      <div style={{ flex: 1 }}>
        <h3 style={{ marginBottom: 8 }}>Clientes logados</h3>
        <ul style={{ background: '#f5f7ff', borderRadius: 8, padding: 12, minHeight: 80 }}>
          {onlineClientes.length === 0 && <li style={{ color: '#64748b' }}>Nenhum cliente logado</li>}
          {onlineClientes.map(u => (
            <li key={u.email} style={{ padding: 4 }}>{u.email}</li>
          ))}
        </ul>
      </div>
    </div>
  );
}
