import React, { useState } from "react";

export default function LoginScreen({ onLogin, onRegister }) {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  async function handleSubmit(e) {
    e.preventDefault();
    setError("");
    if (!email || !password) {
      setError("Preencha todos os campos!");
      return;
    }
    try {
      const res = await fetch("http://localhost:5000/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, password })
      });
      const data = await res.json();
      if (res.ok && data.token) {
        localStorage.setItem("token", data.token);
        onLogin({ email, token: data.token });
      } else {
        setError(data.error || "Login inválido");
      }
    } catch (err) {
      setError("Erro de conexão com o servidor: " + (err.message || err));
    }
  }

  return (
    <div style={{
      display: "flex",
      flexDirection: "column",
      alignItems: "center",
      justifyContent: "center",
      height: "100vh",
      background: "linear-gradient(135deg, #f8fafc 0%, #e0e7ff 50%, #ffe4e6 100%)"
    }}>
      <h1 style={{
        fontFamily: 'Segoe UI, Arial, sans-serif',
        fontWeight: 900,
        fontSize: 48,
        color: '#1e293b',
        marginBottom: 0,
        letterSpacing: 2,
        textShadow: '2px 2px 8px #cbd5e1, 0 2px 0 #64748b, 0 4px 12px #334155'
      }}>CSConnect</h1>
      <div style={{
        fontFamily: 'Segoe UI, Arial, sans-serif',
        fontWeight: 500,
        fontSize: 18,
  color: 'rgba(51, 65, 85, 0.8)',
        marginBottom: 16,
        letterSpacing: 1,
        textShadow: '1px 1px 4px #cbd5e1'
      }}>by CEOsoftware</div>
      <h2 style={{ fontSize: 32, marginBottom: 16, color: '#334155' }}>Login</h2>
      <form onSubmit={handleSubmit} style={{ display: "flex", flexDirection: "column", gap: 16, minWidth: 320 }}>
        <label style={{ fontSize: 20, marginBottom: 4 }}>E-mail</label>
        <input type="email" placeholder="E-mail" value={email} onChange={e => setEmail(e.target.value)} style={{ fontSize: 18, padding: 12, borderRadius: 6, border: '1px solid #ccc' }} />
        <label style={{ fontSize: 20, marginBottom: 4 }}>Senha</label>
        <input type="password" placeholder="Senha" value={password} onChange={e => setPassword(e.target.value)} style={{ fontSize: 18, padding: 12, borderRadius: 6, border: '1px solid #ccc' }} />
        <button type="submit" style={{ fontSize: 20, padding: 12, borderRadius: 6, marginTop: 12 }}>Entrar</button>
      </form>
      {error && <div style={{ color: "red", marginTop: 8 }}>{error}</div>}
      <div style={{ marginTop: 16 }}>
        <span>Não tem conta? <button onClick={onRegister}>Cadastre-se</button></span>
      </div>
    </div>
  );
}
